package com.basic;

public class Add {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int num1=7;
        int num2=5;
        System.out.println("Addition="+num1+num2);
        System.out.println("Addition="+(num1+num2));
        System.out.println("Adittion="+num1+"\t"+num2);
        System.out.println("Adittion=\n"+num1+"\n"+num2);
        System.out.println("Adittion=\r"+num1+"\r"+num2);
	}

}
